import './App.css'
import React, { useState, useEffect } from 'react';
import Cards from "./components/Cards";

const cardImg =[
    {"src":"/img/1.jpg", "matched":false},
    {"src":"/img/2.jpg", "matched":false},
    {"src":"/img/3.jpg", "matched":false},
    {"src":"/img/4.jpg", "matched":false},
    {"src":"/img/5.jpg", "matched":false},
    {"src":"/img/6.jpg", "matched":false},
]
function App() {
    const [cards, setCards] = useState([])
    const [turns, setTurns] = useState(0)
    const [level, setLevel] = useState(1)
    const [choice1, setChoice1] = useState(null)
    const [choice2, setChoice2] = useState(null)
    //shuffle cards
    const shuffleCards = () =>{
        const shuffledCards = [...cardImg,...cardImg]
            .sort(() => Math.random()-0.5)
            .map((card)=>({...card, id: Math.random()}) )
        setCards(shuffledCards)
        setTurns(0)
    }

    //handle a choice
    const handleChoice = (card) => {
        choice1 ? setChoice2(card) : setChoice1(card)
        // console.log(card)
    }

    //reset & add turn
    const oneMove = () => {
        setChoice1(null)
        setChoice2(null)
        setTurns(prevTurns=>prevTurns +1)

    }

    //compare 2 cards
    useEffect(()=>{
        if(choice1 && choice2){
            if(choice1.src === choice2.src){
                //correct
                setCards(prevCards => {
                    return prevCards.map(card =>{
                        if(card.src === choice1.src){
                            return{...card, matched: true}
                        }
                        else{
                            return card
                        }
                    })
                })
                oneMove()
            }
            else {
                setTimeout(()=>oneMove(), 1000)
            }

        }

    },[choice1, choice2])


  return (
    <div className="App">
      <h1>Find Pairs</h1>
        <div>
            <div>Your level: {level}</div>
            <button onClick={shuffleCards}>New Game</button>
        </div>
        {cards && <Cards cards={cards} handleChoice={handleChoice} choice1={choice1} choice2={choice2}/>}
    </div>
  );
}

export default App;
