import './SingleCard.css'
function SingleCard(props) {
    const handleClick = () =>{
        props.handleChoice(props.card);
    }

    return (
            <div className="card" key={props.card.id}>
                <div>
                    {props.flipped && <img className="front"
                         src={props.card.src}
                         alt="card front"/>}
                    {!props.flipped && <img className="back"
                         onClick={handleClick}
                         src="/img/cover.jpg"
                         alt="card back"/>}
                </div>
            </div>


    );
}

export default SingleCard
